contracts/treasury/Strat_Recollateralize.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IWonderfulTreasury.sol";
import "../interfaces/IPool.sol";
import "../interfaces/IWETH.sol";
import "../libs/WethUtils.sol";

contract StratRecollateralize is Ownable {
    using SafeERC20 for IWETH;

    IPool public immutable pool;
    IWonderfulTreasury public immutable treasury;

    constructor(IWonderfulTreasury _treasury, IPool _pool) {
        treasury = _treasury;
        pool = _pool;
    }

    /// @notice Recollateralize the minting pool
    /// @param _amount Amount of ETH will be used to recollateralize
    function recollateralize(uint256 _amount) external onlyOwner {
        require(address(pool) != address(0), "StratRecollateralize::recollateralize:pool not set");
        require(_amount > 0, "StratRecollateralize::recollateralize:invalid amount");
        treasury.requestFund(address(WethUtils.weth), _amount);
        WethUtils.weth.safeTransferFrom(address(treasury), address(this), _amount);
        WethUtils.unwrap(_amount);
        pool.recollateralize{value: _amount}();
        emit Recollateralized(_amount);
    }

    /// @notice fallback for payable -> required to unwrap WETH
    receive() external payable {}

    // EVENTS
    event Recollateralized(uint256 amount);
}

contracts/treasury/Strat_ReduceReserveLP.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IWonderfulTreasury.sol";
import "../libs/WethUtils.sol";

contract StratReduceReserveLP is Ownable {
    using SafeERC20 for IWETH;
    using SafeERC20 for IERC20;

    IWonderfulTreasury public treasury;
    IERC20 public yToken;
    address public yTokenFund;
    IERC20 public lp;
    IUniswapV2Router02 public swapRouter;
    address[] public swapPaths;

    uint256 private constant SLIPPAGE_PRECISION = 1e6;

    constructor(
        IERC20 _yToken,
        address _yTokenFund,
        IERC20 _lp,
        IUniswapV2Router02 _swapRouter,
        address[] memory _swapPaths,
        IWonderfulTreasury _treasury
    ) {
        yToken = _yToken;
        lp = _lp;
        yTokenFund = _yTokenFund;
        swapRouter = _swapRouter;
        swapPaths = _swapPaths;
        treasury = _treasury;
    }

    /* ========== RESTRICTIVE FUNCTIONS ============ */

    /// @notice Remove liqudity, buyback YToken and burn
    /// @param _amount Amount of Liquidity LP token
    function reduceReserve(uint256 _amount, uint256 _minYTokenAmount) external onlyOwner {
        require(_amount > 0, "StratReduceReserveLP::reduceReserve:invalid amount");
        require(yTokenFund != address(0), "StratReduceReserveLP::reduceReserve:invalid address");

        treasury.requestFund(address(lp), _amount);
        lp.safeTransferFrom(address(treasury), address(this), _amount);

        // 1. remove liquidity
        lp.safeIncreaseAllowance(address(swapRouter), _amount);
        swapRouter.removeLiquidity(address(yToken), address(WethUtils.weth), _amount, 0, 0, address(this), block.timestamp);

        // 2. swap Weth -> YToken
        swap(WethUtils.weth.balanceOf(address(this)), _minYTokenAmount);

        // 3. Burn YToken in the contract
        uint256 _yTokenAmt = yToken.balanceOf(address(this));
        yToken.safeTransfer(yTokenFund, _yTokenAmt);

        emit ReserveReduced(_amount, _yTokenAmt);
    }

    /* ========== INTERNAL FUNCTIONS ============ */

    /// @notice Add liquidity for YToken/WETH with the current balance
    function swap(uint256 _wethToSwap, uint256 _minYTokenOut) internal {
        WethUtils.weth.safeIncreaseAllowance(address(swapRouter), _wethToSwap);
        swapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(_wethToSwap, _minYTokenOut, swapPaths, address(this), block.timestamp);
    }

    /* ========== EVENTS ============ */

    event ReserveReduced(uint256 _lpAmount, uint256 _yTokenBurnAmt);
}

contracts/treasury/WonderfulTreasury.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IWonderfulStaking.sol";

/*  WonderfulTreasury.sol ============================================
    The contract is to store the reserve of Wonderful Protocol
    Contract will have a whitelist of strategy contracts which can request funding from Reserve
    These strategy contracts can be used to Allocate fee, Convert reserve to Protocol Owned Liquidity, Recollateralize, etc
==================================================================== */
contract WonderfulTreasury is Ownable {
    using SafeERC20 for IERC20;
    using Address for address;

    mapping(address => bool) public strategies;
    address[] public strategiesArray;
    IWonderfulStaking public staking;

    constructor(IWonderfulStaking _staking) {
        staking = _staking;
    }

    // ========== PUBLIC VIEW FUNCTIONS ============

    /// @notice Return ERC-20 balance of WonderfulTreasury
    /// @param _token Address of the ERC-20 token
    /// @return Balance of the WonderfulTreasury
    function balanceOf(address _token) public view returns (uint256) {
        return IERC20(_token).balanceOf(address(this));
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    /// @notice Request fund from Reserve - only can be called from whitelisted strategies
    /// @param _token Address of the ERC-20 token
    /// @param _amount The requested amount
    function requestFund(address _token, uint256 _amount) external {
        require(strategies[msg.sender], "WonderfulTreasury::requestFund: Only strategies can request fund");
        require(_amount <= balanceOf(_token), "WonderfulTreasury::requestFund: Request more fund than balance");
        IERC20(_token).safeIncreaseAllowance(msg.sender, _amount);
        emit FundRequested(msg.sender, _amount);
    }

    /// @notice Add new strategy
    /// @param _strategy Address of the strategy contract
    function addStrategy(address _strategy) external onlyOwner {
        require(_strategy != address(0), "WonderfulTreasury::addStrategy: invalid address");
        require(!strategies[_strategy], "WonderfulTreasury::addStrategy: strategy was previously added");
        strategies[_strategy] = true;
        strategiesArray.push(_strategy);
        emit StrategyAdded(_strategy);
    }

    /// @notice Remove current strategy
    /// @param _strategy Address of the strategy contract
    function removeStrategy(address _strategy) external onlyOwner {
        require(strategies[_strategy], "WonderfulTreasury::removeStrategy: strategy not found");
        delete strategies[_strategy];

        for (uint256 i = 0; i < strategiesArray.length; i++) {
            if (strategiesArray[i] == _strategy) {
                strategiesArray[i] = address(0);
                // This will leave a null in the array and keep the indices the same
                break;
            }
        }
        emit StrategyRemoved(_strategy);
    }

    /// @notice Allocate protocol's fee to stakers
    /// @param _token Address of ERC-20 token
    /// @param _amount Amount of fee will be distributed
    function allocateFee(address _token, uint256 _amount) external onlyOwner {
        require(address(staking) != address(0), "WonderfulTreasury::allocateFee:Fee distributor not set");
        require(_amount > 0, "WonderfulTreasury::allocateFee: invalid amount");
        IERC20(_token).safeIncreaseAllowance(address(staking), _amount);
        staking.notifyRewardAmount(_token, _amount);
        emit TokenRewardAllocated(_token, _amount);
    }

    // EVENTS
    event StrategyAdded(address indexed _strategy);
    event StrategyRemoved(address indexed _strategy);
    event FundRequested(address indexed _requester, uint256 _amount);
    event TokenRewardAllocated(address indexed _token, uint256 _amount);
}

contracts/zap/WonderfulZapSpookySwap.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "../interfaces/IWonderfulChef.sol";
import "../libs/Babylonian.sol";

/*
    WonderfulZap is a ZapperFi's simplified version of zapper contract which will:
    1. use ETH to swap to target token
    2. make LP between ETH and target token
    3. add into WonderfulChef farm
*/
contract WonderfulZapSpookySwap is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct ZapInfo {
        uint256 pid;
        IERC20 token;
        bool inactive;
    }

    ZapInfo[] public zaps;
    IWonderfulChef public wonderfulChef;
    IUniswapV2Router02 public uniRouter;

    address public constant wethAddress = 0x21be370D5312f44cB42ce377BC9b8a0cEF1A4C83; // WFTM

    constructor(address _wonderfulChef, address _uniRouter) {
        require(_wonderfulChef != address(0), "Zap::constructor: Invalid address");
        require(_uniRouter != address(0), "Zap::constructor: Invalid address");
        wonderfulChef = IWonderfulChef(_wonderfulChef);
        uniRouter = IUniswapV2Router02(_uniRouter);
    }

    // ========= MUTATIVE FUNCTION ==============

    /// @notice Zap function to receive ETH, swap for opposing token,
    /// add liquidity, and deposit LP to farm
    /// @param _zapId: Id of the zap configuration
    /// @param _minLiquidity: Minimum amount of LP received
    /// @param _transferResidual: Flag to determine to transfer dust back to user
    function zap(
        uint256 _zapId,
        uint256 _minLiquidity,
        bool _transferResidual
    ) external payable nonReentrant {
        ZapInfo memory _info = zaps[_zapId];
        require(!_info.inactive, "Zap::zap: Zap configuration is inactive");
        uint256 _ethIn = msg.value;

        address _lp = address(wonderfulChef.lpToken(_info.pid));
        require(_lp != address(0), "Zap::zap: Invalid LP");

        // STEP 1: Swap or Mint token
        (uint256 _ethAmtToSwap, uint256 _tokenAmtToAddLP) = swap(_lp, _ethIn, address(_info.token));

        // STEP 2: Add liquditiy
        uint256 _ethAmtToAddLP = _ethIn - _ethAmtToSwap;
        approveToken(address(_info.token), address(uniRouter), _tokenAmtToAddLP);
        (uint256 _tokenAmtUsedInLp, uint256 _ethAmtUsedInLp, uint256 _liquidity) = uniRouter
            .addLiquidityETH{value: _ethAmtToAddLP}(
            address(_info.token),
            _tokenAmtToAddLP,
            1,
            1,
            address(this),
            block.timestamp
        );
        require(_liquidity >= _minLiquidity, "Zap::zap: Slippage. < minLiquidity");

        // STEP 3: Deposit LP to Farm
        approveToken(_lp, address(wonderfulChef), _liquidity);
        wonderfulChef.deposit(_info.pid, _liquidity, msg.sender);

        // STEP 4: Clean up dust
        if (_transferResidual) {
            if (_tokenAmtToAddLP > _tokenAmtUsedInLp) {
                _info.token.safeTransfer(msg.sender, _tokenAmtToAddLP - _tokenAmtUsedInLp);
            }
            if (_ethAmtToAddLP > _ethAmtUsedInLp) {
                Address.sendValue(payable(msg.sender), _ethAmtToAddLP - _ethAmtUsedInLp);
            }
        }

        emit Zapped(_zapId, _ethIn, _liquidity);
    }

    /// @notice fallback for payable -> required to receive WETH
    receive() external payable {}

    /// @notice Swap internal function to swap from ETH to target Token with calculation
    /// for the appropriate amount of ETH will be used to swap in order to minimize dust
    /// @param _lp: address of the LP pair between WETH and Token
    /// @param _ethIn: Amount of ETH input
    /// @param _token: address of target Token
    function swap(
        address _lp,
        uint256 _ethIn,
        address _token
    ) internal returns (uint256 _ethAmtToSwap, uint256 _tokenAmtReceived) {
        address _token0 = IUniswapV2Pair(_lp).token0();
        (uint256 _res0, uint256 _res1, ) = IUniswapV2Pair(_lp).getReserves();

        if (_token == _token0) {
            _ethAmtToSwap = calculateSwapInAmount(_res1, _ethIn);
        } else {
            _ethAmtToSwap = calculateSwapInAmount(_res0, _ethIn);
        }

        if (_ethAmtToSwap <= 0) _ethAmtToSwap = _ethIn / 2;
        _tokenAmtReceived = doSwapETH(_token, _ethAmtToSwap);
    }

    /// @notice Swap internal function to swap from ETH to target Token
    /// @param _toToken: address of target Token
    /// @param _ethAmt: Amount of ETH input
    function doSwapETH(address _toToken, uint256 _ethAmt)
        internal
        returns (uint256 _tokenReceived)
    {
        address[] memory _path = new address[](2);
        _path[0] = wethAddress;
        _path[1] = _toToken;

        _tokenReceived = uniRouter.swapExactETHForTokens{value: _ethAmt}(
            1,
            _path,
            address(this),
            block.timestamp
        )[_path.length - 1];

        require(_tokenReceived > 0, "Zap::doSwapETH: Error Swapping Tokens 2");
    }

    /// @notice Safe approve spending of an token for an address
    /// @param _token: address of target Token
    /// @param _spender: address of spender
    /// @param _amount: Amount of token can be spent
    function approveToken(
        address _token,
        address _spender,
        uint256 _amount
    ) internal {
        IERC20 _erc20Token = IERC20(_token);
        _erc20Token.safeApprove(_spender, 0);
        _erc20Token.safeApprove(_spender, _amount);
    }

    /// @notice calculate amount to swap just enough so least dust will be leftover when adding liquidity
    /// copied from zapper.fi contract. Assuming 0.2% swap fee
    /// @param _reserveIn: Amount of Reserve for the target token
    /// @param _tokenIn: Amount of total input
    function calculateSwapInAmount(uint256 _reserveIn, uint256 _tokenIn)
        internal
        pure
        returns (uint256)
    {
        return
            (Babylonian.sqrt(_reserveIn * ((_tokenIn * 3992000) + (_reserveIn * 3992004))) -
                (_reserveIn * 1998)) / 1996;
    }

    // ========= RESTRICTIVE FUNCTIONS ==============

    /// @notice Add new zap configuration
    /// @param _token: address of target Token
    /// @param _pid: pid in the target farm
    function addZap(address _token, uint256 _pid) external onlyOwner returns (uint256 _zapId) {
        require(_token != address(0), "Zap::addZap: Invalid address");
        zaps.push(ZapInfo({token: IERC20(_token), pid: _pid, inactive: false}));
        _zapId = zaps.length - 1;

        emit ZapAdded(_zapId, _token, _pid);
    }

    /// @notice Deactivate a Zap configuration
    /// @param _zapId: Id of the zap configuration
    function removeZap(uint256 _zapId) external onlyOwner {
        require(zaps.length > _zapId, "Zap::removeZap: Invalid zapId");
        ZapInfo storage info = zaps[_zapId];
        info.inactive = true;

        emit ZapRemoved(_zapId);
    }

    // ========= EVENTS ==============
    event ZapAdded(uint256 _id, address _token, uint256 _pid);
    event ZapRemoved(uint256 _id);
    event Zapped(uint256 _zapId, uint256 _amount, uint256 _liquidity);
}
